import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

// ÐšÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚ Ð´Ð»Ñ Ð²Ñ‹Ð±Ð¾Ñ€Ð° Ð¼Ð¾Ð´ÐµÐ»Ð¸
const ModelSelector = ({ models, selectedModel, onModelChange }) => {
  const russianModels = models.filter(model => model.language === 'ru');
  const englishModels = models.filter(model => model.language === 'en');

  return (
    <div className="flex-1 mr-4">
      <label className="block text-sm font-medium text-gray-700 mb-2">
        ðŸ¤– Ð’Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð¼Ð¾Ð´ÐµÐ»ÑŒ Ð¿ÐµÑ€ÑÐ¾Ð½Ð°Ð¶Ð°:
      </label>
      <select
        value={selectedModel}
        onChange={(e) => onModelChange(e.target.value)}
        className="w-full p-3 border-2 border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 bg-white text-gray-900"
      >
        <option value="">Ð’Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð¼Ð¾Ð´ÐµÐ»ÑŒ...</option>

        {russianModels.length > 0 && (
          <optgroup label="ðŸ‡·ðŸ‡º Ð ÑƒÑÑÐºÐ¾ÑÐ·Ñ‹Ñ‡Ð½Ñ‹Ðµ Ð¼Ð¾Ð´ÐµÐ»Ð¸">
            {russianModels.map((model) => (
              <option key={model.name} value={model.name}>
                {model.display_name} - {model.country}
              </option>
            ))}
          </optgroup>
        )}

        {englishModels.length > 0 && (
          <optgroup label="ðŸ‡ºðŸ‡¸ ÐÐ½Ð³Ð»Ð¾ÑÐ·Ñ‹Ñ‡Ð½Ñ‹Ðµ Ð¼Ð¾Ð´ÐµÐ»Ð¸">
            {englishModels.map((model) => (
              <option key={model.name} value={model.name}>
                {model.display_name} - {model.country}
              </option>
            ))}
          </optgroup>
        )}
      </select>
    </div>
  );
};

// ÐžÐ±ÑŠÐµÐ´Ð¸Ð½ÐµÐ½Ð½Ñ‹Ð¹ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚ Ð´Ð»Ñ Ñ‚ÐµÑÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ Ð¸ Ð¾Ð±ÑƒÑ‡ÐµÐ½Ð¸Ñ
const TestTrainingComponent = ({ selectedModel }) => {
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');
  const [rating, setRating] = useState(5);
  const [loading, setLoading] = useState(false);

  // Ð”Ð»Ñ Ð¾Ð±ÑƒÑ‡ÐµÐ½Ð¸Ñ
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [priority, setPriority] = useState(5);
  const [trainingLoading, setTrainingLoading] = useState(false);

  // Ð”Ð»Ñ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ñ„Ð°Ð¹Ð»Ð°
  const [file, setFile] = useState(null);
  const [fileLoading, setFileLoading] = useState(false);

  const handleTest = async () => {
    if (!selectedModel || !message.trim()) return;

    setLoading(true);
    try {
      const res = await axios.post(`${API}/test`, {
        message: message.trim(),
        model: selectedModel
      });
      setResponse(res.data.response);
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ñ‚ÐµÑÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ:', error);
      setResponse('âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ð¸ Ð¾Ñ‚Ð²ÐµÑ‚Ð°');
    } finally {
      setLoading(false);
    }
  };

  const handleRating = async () => {
    if (!response || !message.trim()) return;

    try {
      await axios.post(`${API}/rate`, {
        user_id: 'test_user',
        message: message.trim(),
        response: response,
        rating: rating,
        model: selectedModel
      });
      alert('âœ… Ð ÐµÐ¹Ñ‚Ð¸Ð½Ð³ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾!');
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ñ€ÐµÐ¹Ñ‚Ð¸Ð½Ð³Ð°:', error);
      alert('âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ð¸ Ñ€ÐµÐ¹Ñ‚Ð¸Ð½Ð³Ð°');
    }
  };

  const handleTrain = async () => {
    if (!selectedModel || !question.trim() || !answer.trim()) return;

    setTrainingLoading(true);
    try {
      await axios.post(`${API}/train`, {
        question: question.trim(),
        answer: answer.trim(),
        model: selectedModel,
        priority: priority
      });
      alert('âœ… ÐžÐ±ÑƒÑ‡Ð°ÑŽÑ‰Ð¸Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ñ‹!');
      setQuestion('');
      setAnswer('');
      setPriority(5);
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ð±ÑƒÑ‡ÐµÐ½Ð¸Ñ:', error);
      alert('âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ð¸ Ð¾Ð±ÑƒÑ‡Ð°ÑŽÑ‰Ð¸Ñ… Ð´Ð°Ð½Ð½Ñ‹Ñ…');
    } finally {
      setTrainingLoading(false);
    }
  };

  const handleFileUpload = async () => {
    if (!file || !selectedModel) return;

    setFileLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('model', selectedModel);

      const response = await axios.post(`${API}/train-file?model=${selectedModel}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      alert(`âœ… ${response.data.message}`);
      setFile(null);
      // Ð¡Ð±Ñ€Ð°ÑÑ‹Ð²Ð°ÐµÐ¼ input
      const fileInput = document.getElementById('training-file');
      if (fileInput) fileInput.value = '';
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ñ„Ð°Ð¹Ð»Ð°:', error);
      alert('âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ Ñ„Ð°Ð¹Ð»Ð°');
    } finally {
      setFileLoading(false);
    }
  };

  // ÐŸÑ€Ð¸Ð¼ÐµÑ€Ñ‹ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹ Ð´Ð»Ñ Ñ‚ÐµÑÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
  const exampleMessages = [
    "ÐŸÑ€Ð¸Ð²ÐµÑ‚! ÐšÐ°Ðº Ð´ÐµÐ»Ð°?",
    "Ð§Ñ‚Ð¾ Ð»ÑŽÐ±Ð¸ÑˆÑŒ Ð´ÐµÐ»Ð°Ñ‚ÑŒ?",
    "Ð¢Ñ‹ Ð¾Ñ‡ÐµÐ½ÑŒ ÐºÑ€Ð°ÑÐ¸Ð²Ð°Ñ",
    "Ð¥Ð¾Ñ‡Ñƒ Ð¿Ð¾Ð·Ð½Ð°ÐºÐ¾Ð¼Ð¸Ñ‚ÑŒÑÑ Ð¿Ð¾Ð±Ð»Ð¸Ð¶Ðµ",
    "Ð Ð°ÑÑÐºÐ°Ð¶Ð¸ Ð¾ ÑÐµÐ±Ðµ"
  ];

  const handleExampleClick = (exampleMsg) => {
    setMessage(exampleMsg);
  };

  // ÐŸÑ€Ð¸Ð¼ÐµÑ€Ñ‹ Ð´Ð»Ñ Ð¾Ð±ÑƒÑ‡ÐµÐ½Ð¸Ñ
  const trainingExamples = [
    {
      question: "ÐŸÑ€Ð¸Ð²ÐµÑ‚, ÐºÐ°Ðº Ð´ÐµÐ»Ð°?",
      answer: "ÐŸÑ€Ð¸Ð²ÐµÑ‚ ÐºÑ€Ð°ÑÐ°Ð²Ñ‡Ð¸Ðº! Ð£ Ð¼ÐµÐ½Ñ Ð²ÑÐµ Ð¾Ñ‚Ð»Ð¸Ñ‡Ð½Ð¾, Ð¾ÑÐ¾Ð±ÐµÐ½Ð½Ð¾ ÐºÐ¾Ð³Ð´Ð° Ð²Ð¸Ð¶Ñƒ Ñ‚Ð°ÐºÐ¸Ñ… Ð¼Ð¸Ð»Ñ‹Ñ… ðŸ˜˜"
    },
