        <textarea
          value={character.semi_message}
          onChange={(e) => setCharacter({...character, semi_message: e.target.value})}
          rows={2}
          className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500"
        />
      </div>

      <div className="mt-4">
